import React, { useState } from "react";
import "./NewExpenseForm.css";

const NewExpenseForm = () => {
  const [newTitle, setNewTitle] = useState("");
  const [newAmount, setNewAmount] = useState("");
  const [newDate, setNewDate] = useState("");
  // const [userInput, setUserInput] = useState({ newTitle: '',  newAmount: '', newDate: ''})

  const titleChangeHandler = (event) => {
    //setUserInput({...userInput, newTitle: event.target.value})
    setNewTitle(event.target.value);
  };
  const amountChangeHandler = (event) => {
    //setUserInput({...userInput, newAmount: event.target.value})
    setNewAmount(event.target.value);
  };
  const dateChangeHandler = (event) => {
    //setUserInput({...userInput, newDate: event.target.value})
    setNewDate(event.target.value);
  };
  return (
    <div>
      <form action="">
        <div className="new-expense__controls">
          <div className="new-expense__control">
            <label>Title</label>
            <input type="text" onChange={titleChangeHandler} />
          </div>
          <div className="new-expense__control">
            <label>Amount</label>
            <input
              type="number"
              min="0.01"
              step="0.01"
              onChange={amountChangeHandler}
            />
          </div>
          <div className="new-expense__control">
            <label>Date</label>
            <input
              type="date"
              min="2021-01-01"
              step="2023-12-31"
              onChange={dateChangeHandler}
            />
          </div>
        </div>
        <div className="new-expense__actions">
          <button type="submit">Add Expense</button>
        </div>
      </form>
    </div>
  );
};

export default NewExpenseForm;
